package test01;

public class star03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i=0; i<5; i ++) {
			for(int j=0; j<5; j++) {
				if(i == 4|j == 0| i==j )
					System.out.print('*');
				else
					System.out.print(' ');
			}
					System.out.print('\n');
		}
		
	}
	
 }
